using System.ComponentModel.DataAnnotations;

namespace start.Models;

// Die Klasse Person repräsentiert ein Modell mit Validierungsregeln für die Eigenschaften Passwort und RePasswort.
public class Person
{c1::{}}
    // Die Eigenschaft Passwort ist erforderlich und muss zwischen 8 und 12 Zeichen lang sein.
    // Das Attribut  stellt sicher, dass das Feld nicht leer ist.
    // Das Attribut definiert den Anzeigenamen für die Eigenschaft.
    // Das Attribut  legt die minimale und maximale Länge der Zeichenfolge fest.
    //Fehler Meldung "Passwort darf nicht leer sein.
    {{c1::[Required(ErrorMessage="{0} darf nicht leer sein")}}
    {{c1::[Display(Name="Passwort")]}}
    //Fehler Meldung "Passwort: Die Länge muss zwischen 12 und 8 Zeichen liegen")]
    {{c1::[StringLength(12,MinimumLength=8,ErrorMessage ="{0}: Die Länge muss zwischen {2} und {1} Zeichen liegen")]}}
    {{c1::public string? Passwort{get; set;} }}

    // Die Eigenschaft RePasswort ist ebenfalls erforderlich und muss die gleichen Längenbeschränkungen wie Passwort erfüllen.
    // Zusätzlich wird mit dem Attribut  sichergestellt, dass RePasswort mit Passwort übereinstimmt.
   //Fehler Meldung ""RePasswort darf nicht leer sein."
    {{c1::[Required(ErrorMessage="{0} darf nicht leer sein")]}}
    {{c1::[Display(Name="RePasswort")]}}
    //Fehler Meldung "RePasswort: Die Länge muss zwischen 12 und 8 Zeichen liegen")]
    {{c1::[StringLength(12,MinimumLength=8,ErrorMessage="{0}: Die Länge muss zwischen {2} und {1} Zeichen liegen")]}}
    //Fehler Meldung "RePasswort stimmt nicht mit Passwort überein"
    {{c1::[Compare("Passwort",ErrorMessage="{0} stimmt nicht mit {1} überein")]}}
    {{c1::public string? RePasswort {get;set;} }}
{{c1::} }}

</pre>

==HomeController==
<pre>
using System;
using System.ComponentModel.Design;
using Microsoft.AspNetCore.Mvc;
using start.{{c1::Models;}}
namespace start.{{c1::Controllers;}}

// Der HomeController erbt von der Basisklasse Controller, die grundlegende Funktionen für MVC-Controller bereitstellt.
{{c1::public class HomeController : Controller}}
{{c1::{}}
    // Die Index-Methode ist mit der Route "/" verknüpft und wird aufgerufen, wenn die Basis-URL der Anwendung aufgerufen wird.
    {{c1::[Route("/")]}}
    {{c1::public IActionResult Index(Person person)}}
    {{c1::{}}
        // Überprüfung, ob das übergebene Modell (Person) gültig ist.
        {{c1::if (!ModelState.IsValid)}}
        {{c1::{}}
            // Wenn das Modell ungültig ist, werden die Fehler aus dem ModelState extrahiert.
            {{c1::var errors=ModelState.Values}}
            {{c1::.SelectMany(e=>e.Errors)}}
            {{c1::.Select(v=>v.ErrorMessage);}}

            // Rückgabe eines BadRequest (HTTP 400) mit den Fehlern.
            {{c1::return BadRequest(errors);}}
        {{c1::} }}

        // Wenn das Modell gültig ist, wird es als JSON zurückgegeben.
        {{c1::return Json(person);}}
    {{c1::} }}
{{c1::} }}
</pre>

==Grundklasse für Start-Validierung==
<pre>
using System;
using System.ComponentModel.DataAnnotations;

namespace start.praxis;
// Grundklasse für Start-Validierung
// Diese Klasse kann erweitert werden, um spezifische Validierungslogik zu implementieren
// Grundsätzlich wird die Validierung in der abgeleiteten Klasse implementiert
{{c1::public class Start : ValidationAttribute}} // Automaazisch ersellt, um Validierung zu ermöglichen
{
    {{c1::protected override}} ValidationResult? IsValid(object? value, ValidationContext validationContext)
    {
        return base.IsValid(value, validationContext);
    }
}
</pre>

Typo3 ist ein freies Content-Management-System (CMS) für Webseiten, das in PHP geschrieben ist. Es ist besonders bekannt für seine Flexibilität und Skalierbarkeit, was es zu einer beliebten Wahl für Unternehmen und Organisationen macht, die komplexe Websites verwalten möchten. Typo3 bietet eine Vielzahl von Funktionen, darunter:
==Typo3 Installation==
===Mit Composer===
<pre>
cd /var/www
sudo composer create-project "typo3/cms-base-distribution:^13.4" typo3

==Postgrsql Datenbank Einrichten==
<pre>

sudo -u postgres -i
createdb -E UTF8 -O thorsten typo3
psql -d typo3 -c "CREATE EXTENSION postgis;"
psql -d typo3 -c "CREATE EXTENSION hstore;"
psql -d typo3 -c "CREATE EXTENSION pg_trgm;"
psql -d typo3 -c "ALTER TABLE geometry_columns OWNER TO thorsten;"
psql -d typo3 -c "ALTER TABLE spatial_ref_sys OWNER TO thorsten;"
psql -d typo3 -c "GRANT ALL PRIVILEGES ON DATABASE typo3 TO thorsten;"
exit
</pre>
==Nginx Konfiguration==
<pre>
sudo certbot certonly --standalone -d typo3.ahrensburg.city
sudo nano /etc/nginx/conf.d/typo3.conf
</pre>

<pre>
server {
    listen  443 ssl http2;
    listen [::]:443 ssl http2;
    server_name typo3.ahrensburg.city;
     # SSL certificates
    ssl_certificate /etc/letsencrypt/live/typo3.ahrensburg.city/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/typo3.ahrensburg.city/privkey.pem;

    root /var/www/typo3/public;
    index index.php;

    # TYPO3 specific rewrite rules
    location / {
        try_files $uri $uri/ /index.php$is_args$args;
    }

    # Pass PHP scripts to PHP-FPM
    location ~ \.php$ {
        include snippets/fastcgi-php.conf;
        fastcgi_pass unix:/run/php/php-fpm.sock; # Adjust if your PHP-FPM socket is different (e.g., php8.3-fpm.sock)
        fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
        fastcgi_read_timeout 300; # Increase if you experience timeouts during TYPO3 installation/operations
    }

# Special root site case. prevent "try_files $uri/" + "index" from skipping the cache
# by accessing /index.php directly
location =/ {
    recursive_error_pages on;
    error_page 405 = @sfc;
    return 405;
}

location @t3frontend {
    # Using try_files for ease of configuration demonstration here,
    # you can also fastcgi_pass directly to php here
    try_files $uri /index.php$is_args$args;
}

location @sfc {
    # Perform an internal redirect to TYPO3 if any of the required
    # conditions for StaticFileCache don't match
    error_page 405 = @t3frontend;

    # Query String needs to be empty
    if ($args != '') {
        return 405;
    }

    # We can't serve static files for logged-in BE/FE users
    if ($cookie_staticfilecache = 'typo_user_logged_in') {
        return 405;
    }

    if ($cookie_be_typo_user != '') {
        return 405;
    }

    # Ensure we redirect to TYPO3 for non GET/HEAD requests
    if ($request_method !~ ^(GET|HEAD)$ ) {
        return 405;
    }

    # Disable cache for EXT:solr indexing requests
    if ($http_x_tx_solr_iq) {
        return 405;
    }

    charset utf-8;
    default_type text/html;
    try_files /typo3temp/assets/tx_staticfilecache/${scheme}_${host}_${server_port}${uri}/index
          /typo3temp/assets/tx_staticfilecache/${scheme}_${host}_${server_port}${uri}
          =405;
}

location /typo3temp/assets/tx_staticfilecache {
    deny all;
}

    # Optional: Enable Gzip compression
    gzip on;
    gzip_vary on;
    gzip_proxied any;
    gzip_comp_level 6;
    gzip_buffers 16 8k;
    gzip_http_version 1.1;
    gzip_types text/plain text/css application/json application/javascript text/xml application/xml application/xml+rss text/javascript;

    # Optional: Add security headers
    add_header X-Frame-Options "SAMEORIGIN";
    add_header X-Content-Type-Options "nosniff";
    add_header X-XSS-Protection "1; mode=block";
    add_header Referrer-Policy "no-referrer-when-downgrade";
    add_header Strict-Transport-Security "max-age=31536000; includeSubDomains; preload" always;
}
</pre>

