    {{c1::[Route("/start/{id}")]}}
    {{c1::public IActionResult Index ([FromRoute] int id)}}
    {{c1::{}}
        // Gibt die übergebene ID als Inhalt der HTTP-Antwort zurück
        {{c1::return Content($"{id}");}}
    {{c1::} }}

{{c1::} }}

